package Week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int i=1; 
   int fact=1;
   int no =5;
   for (;i<=no;i++)
   {
	   fact=fact*i;
	   
	  	  
   }
   System.out.println("outside the for loop factorial of 1 to 5 numbers");
   System.out.println(fact);
	}

}
